function OnUpdate(doc, meta) {
    const url_encode_versions = ['6.6.2', '7.1.0', '7.2.0'];
    for (var version of url_encode_versions) {
        var request1 = {
            path : 'get',
            params : {
                'foo1' : 'bar1',
                'foo2' : 'bar!2',
                'foo3' : 'bar+3',
                'foo4' : 'bar+@4',
                'foo5' : 'abcdef$123'
            },
            url_encode_version : version

        };
        var request2 = {
            path : 'search',
            params : {
                'jql' : 'project=MB',
                'type' : 'Improvement',
                'startAt' : '0',
                'maxResults' : '0'
            },
            url_encode_version : version

        };
        try {
        	var response1 = curl("GET", postman, request1);
        	var response2 = curl("GET", jira, request2);
        	log('response1 status received from server:', response1.status);
        	log('response2 status received from server:', response2.status);
        	// retry in case postman echo returns 502 error on a rare basis
        	if(response1.status == 502) {
        	    var response1 = curl("GET", postman, request1);
        	    log('response1 status received from server:', response1.status);
        	}
        	if(response1.status == 200 && (response2.status == 200 || (response2.status == 400 && version == '7.1.0'))) {
        	    var time_rand = random_gen();
        	    couchbase.insert(dst_bucket,{"id" : meta.id + time_rand}, 'hello world');
        	}
        }
        catch (e) {
        	log('error:', e);
        }
    }
}

function random_gen(){
    var rand = Math.floor(Math.random() * 20000000) * Math.floor(Math.random() * 20000000);
    var time_rand = Math.round((new Date()).getTime() / 1000) + rand;
    return time_rand;
}
